fw.stringToXml=function(e){var t;e=e.replace(/[\t\n]/gi,"");if(typeof window.DOMParser!="undefined")t=(new window.DOMParser).parseFromString(e,"text/xml");else if(typeof window.ActiveXObject!="undefined"&&new window.ActiveXObject("Microsoft.XMLDOM")){t=new window.ActiveXObject("Microsoft.XMLDOM");t.async="false";t.loadXML(e)}else t=undefined;return t};fw.xmlToJson=function(e){var t={},n=e;if(typeof e=="string"){n=this.stringToXml(e);if(!n)return t}if(n.nodeType===1){if(n.attributes.length>0){t["@attributes"]={};for(var r=0;r<n.attributes.length;r++){var i=n.attributes.item(r);t["@attributes"][i.nodeName]=i.nodeValue}}}else n.nodeType===3&&(t=n.nodeValue);if(n.hasChildNodes())for(var s=0;s<n.childNodes.length;s++){var o=n.childNodes.item(s),u=o.nodeName;typeof t[u]=="undefined"&&(t[u]=this.xmlToJson(o));if(typeof t[u].push=="undefined"){var a=t[u];t[u]=[];t[u].push(a)}else t[u].push(this.xmlToJson(o))}return t};